mapper.select:
  		SELECT MEMBER_EMAIL 
		FROM "MEMBER"
		WHERE MEMBER_NICKNAME = #{memberNickname}
		AND MEMBER_TEL = #{memberTel}
		AND MEMBER_DEL_FL = 'N'
		AND AUTHORITY = 'N'

  		SELECT MEMBER_PW 
		FROM "MEMBER"
		WHERE MEMBER_NICKNAME = #{memberNickname}
		AND MEMBER_TEL = #{memberTel}
		AND MEMBER_EMAIL = #{memberEmail}
		AND MEMBER_DEL_FL = 'N'
		AND AUTHORITY = 'N'
